import React, { useEffect, useState } from 'react';
import "../css/PageGlobal.css";
import "../css/CheckOrder.css";
import { useUser } from '../UserContext';
import { apiUrl } from '../config/BeApiEndpoint';
const CheckOrder = () => {
    const { user } = useUser();
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        // Hàm lấy danh sách order từ API
        const fetchOrders = async () => {
            try {
                // Gọi API để lấy danh sách order
                const response = await fetch(`${apiUrl}/OrderList/siteCode/${user.siteCode}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch orders.');
                }
                const data = await response.json();
                // Cập nhật state với danh sách order từ API
                setOrders(data.data);
            } catch (error) {
                console.error('Error fetching orders:', error);
                // Xử lý lỗi nếu cần
            }
        };

        // Gọi hàm lấy danh sách order khi component được mount
        fetchOrders();
    }, []);

    // Kiểm tra quyền truy cập của người dùng
    if (user?.role !== 1) {
        return (
            <div>
                <h1>404 Not Found</h1>
            </div>
        );
    }
    console.log("orders", orders)
    return (
        <div>
            <h1>Check Order</h1>
            {/* Hiển thị danh sách order */}
            <ul>
    {orders && orders.map(order => (
        <li key={order.id}>
            {/* Hiển thị thông tin của mỗi order */}
            {order.orderNumber} - {order.status}
        </li>
    ))}
</ul>

        </div>
    );
};

export default CheckOrder;
