import React, { useState } from 'react';

const data = [
    {
        "inventory": {
            "inventory_id": 2001,
            "merchandiseCode": 1,
            "inStockQuantity": 300,
            "unit": "piece",
            "siteCode": 101
        },
        "site": {
            "site_code": 101,
            "siteName": "Hanoi",
            "byShip": 500,
            "byAir": 300,
            "otherInfo": "Main warehouse"
        }
    },
    {
        "inventory": {
            "inventory_id": 2002,
            "merchandiseCode": 2,
            "inStockQuantity": 500,
            "unit": "piece",
            "siteCode": 102
        },
        "site": {
            "site_code": 102,
            "siteName": "Ho Chi Minh",
            "byShip": 400,
            "byAir": 200,
            "otherInfo": "Secondary site"
        }
    },
    {
        "inventory": {
            "inventory_id": 2006,
            "merchandiseCode": 2,
            "inStockQuantity": 150,
            "unit": "piece",
            "siteCode": 101
        },
        "site": {
            "site_code": 101,
            "siteName": "Hanoi",
            "byShip": 500,
            "byAir": 300,
            "otherInfo": "Main warehouse"
        }
    }
];

const SiteInventoryItem = ({ data = data }) => {
    // Tạo một đối tượng để nhóm dữ liệu theo site_code
    const groupedData = data.reduce((acc, currentItem) => {
      const { site_code } = currentItem.site;
      if (!acc[site_code]) {
        acc[site_code] = [];
      }
      acc[site_code].push(currentItem);
      return acc;
    }, {});
  
    return (
      <div>
        {Object.keys(groupedData).map(siteCode => (
          <div key={siteCode}>
            <h2>Site Code: {siteCode}</h2>
            <table>
              <thead>
                <tr>
                  <th>Inventory ID</th>
                  <th>Merchandise Code</th>
                  <th>In Stock Quantity</th>
                  <th>Unit</th>
                  <th>Site Name</th>
                  <th>By Ship</th>
                  <th>By Air</th>
                  <th>Other Info</th>
                </tr>
              </thead>
              <tbody>
                {groupedData[siteCode].map(item => (
                  <tr key={item.inventory.inventory_id}>
                    <td>{item.inventory.inventory_id}</td>
                    <td>{item.inventory.merchandiseCode}</td>
                    <td>{item.inventory.inStockQuantity}</td>
                    <td>{item.inventory.unit}</td>
                    <td>{item.site.siteName}</td>
                    <td>{item.site.byShip}</td>
                    <td>{item.site.byAir}</td>
                    <td>{item.site.otherInfo}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    );
  };
  
  export default SiteInventoryItem;
