import React, { useState } from 'react';

const SiteInventoryItem = ({ data = [] }) => {
    const [selectedSite, setSelectedSite] = useState(null);

    // Hàm xử lý sự kiện khi người dùng ấn vào site
    const handleSiteClick = (siteCode) => {
        setSelectedSite(siteCode);
    };

    return (
        <div>
            {data.map(item => (
                <div key={item.site.site_code}>
                    <h2 onClick={() => handleSiteClick(item.site.site_code)}>Site Code: {item.site.site_code}</h2>
                    {/* Hiển thị thông tin merchandise khi site được chọn */}
                    {selectedSite === item.site.site_code && (
                        <table>
                            <thead>
                                <tr>
                                    <th>Inventory ID</th>
                                    <th>Merchandise Code</th>
                                    <th>In Stock Quantity</th>
                                    <th>Unit</th>
                                    <th>Site Name</th>
                                    <th>By Ship</th>
                                    <th>By Air</th>
                                    <th>Other Info</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr key={item.inventory.inventory_id}>
                                    <td>{item.inventory.inventory_id}</td>
                                    <td>{item.inventory.merchandiseCode}</td>
                                    <td>{item.inventory.inStockQuantity}</td>
                                    <td>{item.inventory.unit}</td>
                                    <td>{item.site.siteName}</td>
                                    <td>{item.site.byShip}</td>
                                    <td>{item.site.byAir}</td>
                                    <td>{item.site.otherInfo}</td>
                                </tr>
                            </tbody>
                        </table>
                    )}
                </div>
            ))}
        </div>
    );
};

export default SiteInventoryItem;
