import React from 'react';
import '../css/Order.css';
import { useUser } from '../UserContext';
import { useLocation } from 'react-router-dom';


const Order = () => {
    const { user } = useUser();
    const location = useLocation();
    const merchandiseList = location.state?.merchandiseList || [];

    console.log("Order merchandiseList", merchandiseList);
    const getInventoryAndSites = async (merchandiseCode) => {
        try {
            const response = await fetch(`http://localhost:8080/inventory-and-sites/${merchandiseCode}`);
            console.log(response.data); // Xử lý dữ liệu trả về từ API ở đây
        } catch (error) {
            console.error('Error fetching inventory and sites:', error);
        }
    };
    return (
        <div>
            {user?.role === 0 ? (
                <div>
                    <h1>Order</h1>
                </div>
            ) : null}
        </div>
    );
};

export default Order;
