import React, { useState, useEffect } from "react";
import { apiUrl } from "../../config/BeApiEndpoint";
import MerchandiseItem from "../MerchandiseItem/MerchandiseItem";

const SearchMerchandise = ({ searchMerchandise }) => {
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [isSearchFocused, setIsSearchFocused] = useState(true);

    const handleSearch = async () => {
        try {
            const response = await fetch(`${apiUrl}/Merchandise/name/${searchTerm}`, {
                method: 'GET',
                headers: {
                    "Content-Type": "application/json",
                },
            });
            const data = await response.json();
            if (data.status === "success") {
                setSearchResults(data.data);
            } else {
                alert(data.message);
            }
        } catch (error) {
            console.error("Có lỗi xảy ra khi tìm kiếm: ", error);
        }
    };

    const handleChange = (e) => {
        setSearchTerm(e.target.value);
        // Thực hiện tìm kiếm ngay khi người dùng nhập liệu
        handleSearch();
    };

    useEffect(() => {
        // Chỉ thực hiện tìm kiếm khi isSearchFocused thay đổi
        if (isSearchFocused) {
            handleSearch();
        }
    }, [isSearchFocused]);

    return (
        <div>
            <input
                id="search-container"
                type="text"
                placeholder="Nhập tên hàng hóa cần tìm"
                value={searchTerm}
                onChange={handleChange}
                onFocus={() => setIsSearchFocused(true)}
                onBlur={() => setIsSearchFocused(false)}
            />
            {/* Hiển thị kết quả tìm kiếm */}
            <div>
                Search Results:
                {searchResults.length > 0 && sẻarchTerm ? (
                    searchResults.map((merchandise) => (
                        <MerchandiseItem key={merchandise.merchandise_code} merchandise={merchandise} />
                    ))
                ) : (
                    <p>Không tìm thấy kết quả.</p>
                )}
            </div>
        </div>
    );
}

export default SearchMerchandise;
