import React, { useEffect, useState } from 'react';
import "../css/PageGlobal.css";
import "../css/CheckOrder.css";
import { useUser } from '../UserContext';
import { apiUrl } from '../config/BeApiEndpoint';
const CheckOrder = () => {
    const { user } = useUser();
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        // Hàm lấy danh sách order từ API
        const fetchOrders = async () => {
            try {
                // Gọi API để lấy danh sách order
                const response = await fetch(`${apiUrl}/OrderList/siteCode/${user.siteCode}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch orders.');
                }
                const data = await response.json();
                // Cập nhật state với danh sách order từ API
                setOrders(data.data);
            } catch (error) {
                console.error('Error fetching orders:', error);
                // Xử lý lỗi nếu cần
            }
        };

        // Gọi hàm lấy danh sách order khi component được mount
        fetchOrders();
    }, []);

    const handleOrderStatusUpdate = async (orderListId) => {
        try {
            const response = await fetch(`${apiUrl}/OrderList/${orderListId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    status: 'sent',
                }),
            });
            if (!response.ok) {
                throw new Error('Failed to update order status.');
            }
            const updatedOrder = await response.json();
            setOrders((prevOrders) => prevOrders.map((order) => {
                if (order.orderListId === updatedOrder.data.orderListId) {
                    return updatedOrder.data;
                }
                return order;
            }));
        } catch (error) {
            console.error('Error updating order status:', error);
            // Xử lý lỗi nếu cần
        }
    }

    // Kiểm tra quyền truy cập của người dùng
    if (user?.role !== 1) {
        return (
            <div>
                <h1>404 Not Found</h1>
            </div>
        );
    }
    console.log("orders", orders)
    return (
        <div className='check-order'>
            <h1>Check Order</h1>
            {/* Hiển thị danh sách order */}
            <table>
    <thead>
        <tr>
            <th>Order List ID</th>
            <th>Site Code</th>
            <th>Merchandise Code</th>
            <th>Quantity</th>
            <th>Unit</th>
            <th>Delivery Means</th>
            <th>Status</th>
        </tr>
    </thead>
    <tbody>
        {orders && orders.map((order, index) => (
            <tr key={index}>
                <td>{order.orderListId}</td>
                <td>{order.siteCode}</td>
                <td>{order.merchandiseCode}</td>
                <td>{order.quantity}</td>
                <td>{order.unit}</td>
                <td>{order.deliveryMeans}</td>
                <td>{order.status}</td>
                <td><button onClick={()=>{
                    
                }}>Đã gửi</button></td>
            </tr>
        ))}
    </tbody>
</table>


        </div>
    );
};

export default CheckOrder;
