const SiteInventoryItem = ({ siteInventoryItem, handleInputChange }) => {
    const { siteInventoryItem_code, siteInventoryItemName, quantity, price } = siteInventoryItem;
    return (
        <tr>
            <td>{siteInventoryItem_code}</td>
            <td>{siteInventoryItemName}</td>
            <td>{quantity}</td>
            <td>{price}</td>
            <td>
                <input
                    type='number'
                    min='0'
                    value={quantity}
                    onChange={(e) => handleInputChange(siteInventoryItem_code, e.target.value)}
                />
            </td>
        </tr>
    );
}
export default SiteInventoryItem;
