import React from 'react';
import { useState, useEffect } from 'react';
import { useUser } from '../UserContext';
import { apiUrl } from '../config/BeApiEndpoint';
import "../css/PageGlobal.css";
import "../css/Home.css";
import { Link } from 'react-router-dom';
import SearchMerchandise from '../components/SearchMerchandise/SearchMerchandise';
const Home = () => {
const {user}=useUser();
const siteCode = user?.siteCode;
// lấy thông tin về site 
const [site, setSite] = useState({});
const [error, setError] = useState(null);
const [searchMerchandise,setSearchMerchandise]=useState([]);
useEffect(() => {
    const fetchSite = async () => {
        try {
            const response = await fetch(`${apiUrl}/Site/${siteCode}`);
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            const data = await response.json();
            setSite(data.data);
        } catch (error) {
            setError(error.message);
            console.error("Lỗi khi lấy thông tin site: ", error);
        }
    };

    if (siteCode) {
        fetchSite();
    }
}, [siteCode]); // Correct placement of the dependency array

if (error) {
    return <div>Error: {error}</div>;
}

if (!site) {
    return <div>Loading...</div>;
}
if (user?.role===1) {
  return (
    <div className='home-page'>
      <h1>Chào mừng tới trang quản trị</h1>
    <div>
        <h2>Thông tin site</h2>
        <table className='site-table'>
                    <tbody>
                        <tr>
                            <th>Mã site</th>
                            <td>{site.site_code}</td>
                        </tr>
                        <tr>
                            <th>Tên site</th>
                            <td>{site.siteName}</td>
                        </tr>
                        <tr>
                            <th>Vận chuyển bằng thuyền</th>
                            <td>{site.byShip} ngày</td>
                        </tr>
                        <tr>
                            <th>Vận chuyển bằng máy bay</th>
                            <td>{site.byAir} ngày</td>
                        </tr>
                    </tbody>
                </table>
    </div>
    </div>
  );}
  if(user?.role===0){
    return(
        <div>
            <SearchMerchandise />
        </div>
    )
  }
  if(user === null){
    return(
        <div>
            <h1>Bạn cần đăng nhập</h1>
            <Link to="/login">Đăng nhập</Link>
        </div>
    )
  }
}
export default Home;
