import React, { useState } from 'react';

const SiteInventoryItem = ({ data }) => {
    // Tạo một đối tượng để nhóm dữ liệu theo site_code
    const groupedData = data.reduce((acc, currentItem) => {
        const { site_code } = currentItem.site;
        if (!acc[site_code]) {
            acc[site_code] = [];
        }
        acc[site_code].push(currentItem);
        return acc;
    }, {});

    // State để lưu trạng thái hiển thị merchandise
    const [selectedMerchandise, setSelectedMerchandise] = useState(null);

    // Hàm xử lý sự kiện khi người dùng ấn vào merchandise
    const handleMerchandiseClick = (inventoryId) => {
        setSelectedMerchandise(inventoryId);
    };

    return (
        <div>
            {Object.keys(groupedData).map(siteCode => (
                <div key={siteCode}>
                    <h2>Site Code: {siteCode}</h2>
                    <table>
                        <thead>
                            <tr>
                                <th>Inventory ID</th>
                                <th>Merchandise Code</th>
                                <th>In Stock Quantity</th>
                                <th>Unit</th>
                                <th>Site Name</th>
                                <th>By Ship</th>
                                <th>By Air</th>
                                <th>Other Info</th>
                            </tr>
                        </thead>
                        <tbody>
                            {groupedData[siteCode].map(item => (
                                <tr key={item.inventory.inventory_id} onClick={() => handleMerchandiseClick(item.inventory.inventory_id)}>
                                    <td>{item.inventory.inventory_id}</td>
                                    <td>{item.inventory.merchandiseCode}</td>
                                    <td>{item.inventory.inStockQuantity}</td>
                                    <td>{item.inventory.unit}</td>
                                    <td>{item.site.siteName}</td>
                                    <td>{item.site.byShip}</td>
                                    <td>{item.site.byAir}</td>
                                    <td>{item.site.otherInfo}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    {/* Hiển thị thông tin chi tiết của merchandise nếu được chọn */}
                    {selectedMerchandise && selectedMerchandise === groupedData[siteCode].inventory.inventory_id && (
                        <div>
                            <h3>Selected Merchandise</h3>
                            <p>Inventory ID: {groupedData[siteCode].inventory.inventory_id}</p>
                            <p>Merchandise Code: {groupedData[siteCode].inventory.merchandiseCode}</p>
                            <p>In Stock Quantity: {groupedData[siteCode].inventory.inStockQuantity}</p>
                            <p>Unit: {groupedData[siteCode].inventory.unit}</p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default SiteInventoryItem;
