import React, { useState, useEffect } from 'react';
import { useUser } from '../UserContext';
import { useLocation } from 'react-router-dom';
import { apiUrl } from '../config/BeApiEndpoint';

const Order = () => {
    const { user } = useUser();
    const location = useLocation();
    const [inventoryAndSitesList, setInventoryAndSitesList] = useState([]);
    const merchandiseList = location.state?.merchandiseList || [];

    console.log("Order merchandiseList", merchandiseList);

    const getInventoryAndSites = async (merchandiseCode) => {
        try {
            const response = await fetch(`${apiUrl}/Inventory/merchandiseCode/sites/${merchandiseCode}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                }
            }).then(response => response.json())
            .then(data => {
                const resultData = data.data;
                // Sử dụng một mảng tạm thời để lưu trữ các phần tử mới không trùng lặp
            const newItems = data.data.filter(item => 
                !inventoryAndSitesList.some(existingItem => 
                    existingItem.inventory.inventory_id === item.inventory.inventory_id &&
                    existingItem.site.site_code === item.site.site_code
                )
            );

            // Thêm các phần tử mới không trùng lặp vào danh sách
            if (newItems.length > 0) {
                setInventoryAndSitesList(prevList => [...prevList, ...newItems]);
            }

            newItems.forEach(item => {
                console.log("item site code", item.inventory.inventory_id);
            });
            });
            

        } catch (error) {
            console.error('Error fetching inventory and sites:', error);
        }
    };
    console.log("inventoryAndSitesList",inventoryAndSitesList)
    useEffect(() => {
        // Gọi API khi component được render
        merchandiseList.forEach(merchandise => {
            getInventoryAndSites(merchandise.merchandise_code);
        });
    }, [merchandiseList]);

    return (
        <div>
            {user?.role === 0 ? (
                <div>
                    <h1>Order</h1>
                </div>
            ) : null}
        </div>
    );
};

export default Order;
