const SearchMerchandise = ({ searchMerchandise }) => {
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [isSearchFocused, setIsSearchFocused] = useState(true);
  
    const handleSearch = async () => {
      try {
        const response = await fetch(
          `${APIpath}search/words?searchTerm=${searchTerm}`,
        );
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
  
        const data = await response.json();
        setSearchResults(data);
        onSearchResults(data);
      } catch (error) {
        console.error("Error searching courses:", error);
      }
    };
  
    const handleFocus = () => {
      setIsSearchFocused(true);
    };
  
    const handleBlur = () => {
      setIsSearchFocused(false);
      handleSearch();
    };
  
    const handleChange = (e) => {
      setSearchTerm(e.target.value);
    };
  
    useEffect(() => {
      // Chỉ thực hiện tìm kiếm nếu ô tìm kiếm đang được focus
      if (isSearchFocused) {
        handleSearch();
      }
    }, [searchTerm, isSearchFocused]);
  
    return (
      <div>
        <input
          id="search-container"
          type="text"
          placeholder="Nhập tên, cách phát âm hoặc nghĩa của từ"
          value={searchTerm}
          onChange={handleChange}
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
      </div>
    );
}
export default SearchMerchandise;
