import React, { useState, useEffect } from "react";
import { apiUrl } from "../../config/BeApiEndpoint";

const SearchMerchandise = ({ searchMerchandise }) => {
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [isSearchFocused, setIsSearchFocused] = useState(true);
    console.log(searchResults)
    const handleSearch = async () => {
      try {
       const response = await fetch(`${apiUrl}/Merchandise/name/${searchTerm}`,{
        method: 'GET',
        headers: {
          "Content-Type": "application/json",
        },
       }).then((res) => res.json())
       .then((data) => {
           if (data.status === "success") {
               setSearchResults(data.data);
           } else {
               alert(data.message);
           }
       });

    } catch (error) {
        console.error("Có lỗi xảy ra khi tìm kiếm: ", error);
    }
    }

    const handleFocus = () => {
      setIsSearchFocused(true);
    };
  
    const handleBlur = () => {
      setIsSearchFocused(false);
      handleSearch();
    };
  
    const handleChange = (e) => {
      setSearchTerm(e.target.value);
    };
  
    useEffect(() => {
      // Chỉ thực hiện tìm kiếm nếu ô tìm kiếm đang được focus
      if (isSearchFocused) {
        handleSearch();
      }
    }, [searchTerm, isSearchFocused]);
  
    return (
      <div>
        <input
          id="search-container"
          type="text"
          placeholder="Nhập tên hàng hóa cần tìm"
          value={searchTerm}
          onChange={handleChange}
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
      </div>
    );
}

export default SearchMerchandise;
